import React, { useRef, useEffect, useState } from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity, SafeAreaView, Animated, Modal, ActivityIndicator } from 'react-native';
import { auth, db } from '../firebaseConfig';
import { doc, getDoc } from 'firebase/firestore';
import { useFocusEffect } from '@react-navigation/native';
import {
  screenWidth as width,
  screenHeight as height,
  wp,
  hp,
  SPACING,
  FONT_SIZES,
  scaleDimension,
  scaleFont,
  getResponsiveDimension
} from '../utils/dimensions';

const pixelFont = 'PressStart2P_400Regular';

const logoSnake = require('../assets/snake.png');
const questionSymbol = require('../assets/question-symbol.png');

export default function SnakeDetailScreen({ navigation }) {
  const [stats, setStats] = useState({
    bestScore: 0,
    lastScore: 0,
    totalGames: 0,
    totalPixels: 0
  });
  const [isLoading, setIsLoading] = useState(true);
  const [showHelp, setShowHelp] = useState(false);

  useFocusEffect(
    React.useCallback(() => {
      let isActive = true;
      const loadStats = async () => {
        if (!auth.currentUser) return;
        try {
          const statsRef = doc(db, 'users', auth.currentUser.uid, 'snakeStats', 'stats');
          const statsDoc = await getDoc(statsRef);
          if (statsDoc.exists() && isActive) {
            setStats({
              bestScore: statsDoc.data().bestScore || 0,
              lastScore: statsDoc.data().lastScore || 0,
              totalGames: statsDoc.data().totalGames || 0,
              totalPixels: statsDoc.data().totalPixels || 0,
            });
          }
        } catch (error) {
          console.error('Error loading stats:', error);
        } finally {
          if (isActive) setIsLoading(false);
        }
      };
      setIsLoading(true);
      loadStats();
      return () => { isActive = false; };
    }, [])
  );

  return (
    <SafeAreaView style={styles.safeArea}>
      {/* Back button */}
      <TouchableOpacity 
        style={styles.backButton} 
        onPress={() => navigation.goBack()}
      >
        <View style={styles.backBox}>
          <Text style={styles.backText}>←</Text>
        </View>
      </TouchableOpacity>

      <View style={styles.container}>
        {/* Title */}
        <Text style={styles.title}>SNAKE</Text>

        {/* Snake Logo */}
        <View style={styles.logoContainer}>
          <View style={styles.logoOuterBorder}>
            <View style={styles.logoInnerBorder}>
              <Image 
                source={logoSnake} 
                style={styles.logo} 
                resizeMode="contain"
              />
            </View>
          </View>
        </View>

        {/* Stats */}
        {isLoading ? (
          <ActivityIndicator size="large" color="#00fff7" />
        ) : (
          <View style={styles.statsContainer}>
            <View style={styles.statRow}>
              <Text style={styles.statLabel}>Mejor Puntuación:</Text>
              <Text style={styles.statValue}>{stats.bestScore}</Text>
            </View>
            <View style={styles.statRow}>
              <Text style={styles.statLabel}>Última puntuación</Text>
              <Text style={styles.statValue}>{stats.lastScore}</Text>
            </View>
            <View style={styles.statRow}>
              <Text style={styles.statLabel}>Partidas totales:</Text>
              <Text style={styles.statValue}>{stats.totalGames}</Text>
            </View>
            <View style={styles.statRow}>
              <Text style={styles.statLabel}>Total píxeles comidos:</Text>
              <Text style={styles.statValue}>{stats.totalPixels}</Text>
            </View>
          </View>
        )}

        {/* Help Button */}
        <TouchableOpacity 
          style={styles.helpButton}
          onPress={() => setShowHelp(true)}
        >
          <Text style={styles.helpButtonText}>?</Text>
        </TouchableOpacity>

        {/* Start Button */}
        <TouchableOpacity 
          style={styles.startButton}
          onPress={() => navigation.navigate('SnakeGameScreen')}
        >
          <Text style={styles.startButtonText}>¡START!</Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  safeArea: {
    flex: 1,
    backgroundColor: '#0a0a23',
  },
  container: {
    flex: 1,
    alignItems: 'center',
    paddingTop: height * 0.05,
    paddingHorizontal: width * 0.05,
  },
  backButton: {
    position: 'absolute',
    top: height * 0.02,
    left: width * 0.04,
    zIndex: 10,
  },
  backBox: {
    width: 40,
    height: 40,
    borderRadius: 8,
    backgroundColor: '#3a2172',
    borderWidth: 2,
    borderColor: '#ff2e7e',
    alignItems: 'center',
    justifyContent: 'center',
    shadowColor: '#ff2e7e',
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: 0.5,
    shadowRadius: 8,
  },
  backText: {
    color: '#ff2e7e',
    fontFamily: pixelFont,
    fontSize: 24,
  },
  title: {
    fontFamily: pixelFont,
    fontSize: scaleFont(42),
    color: '#00fff7',
    marginBottom: height * 0.03,
    textShadowColor: 'rgba(0, 255, 247, 0.5)',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 10,
  },
  logoContainer: {
    width: width * 0.5,
    aspectRatio: 1,
    marginBottom: height * 0.03,
  },
  logoOuterBorder: {
    flex: 1,
    padding: 2,
    borderRadius: 20,
    backgroundColor: '#ff2e7e',
  },
  logoInnerBorder: {
    flex: 1,
    padding: 2,
    borderRadius: 18,
    backgroundColor: '#00fff7',
    overflow: 'hidden',
  },
  logo: {
    width: '100%',
    height: '100%',
    backgroundColor: '#0a0a23',
  },
  statsContainer: {
    width: '100%',
    marginBottom: height * 0.03,
  },
  statRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 8,
  },
  statLabel: {
    fontFamily: pixelFont,
    fontSize: scaleFont(12),
    color: '#00fff7',
    textShadowColor: 'rgba(0, 255, 247, 0.5)',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 8,
  },
  statValue: {
    fontFamily: pixelFont,
    fontSize: scaleFont(12),
    color: '#00fff7',
    textShadowColor: 'rgba(0, 255, 247, 0.5)',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 8,
  },
  helpButton: {
    width: 40,
    height: 40,
    borderRadius: 20,
    backgroundColor: '#0a0a23',
    borderWidth: 2,
    borderColor: '#ff2e7e',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: height * 0.02,
  },
  helpButtonText: {
    fontFamily: pixelFont,
    fontSize: scaleFont(20),
    color: '#ff2e7e',
    textShadowColor: 'rgba(255, 46, 126, 0.5)',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 8,
  },
  startButton: {
    width: width * 0.8,
    height: 60,
    borderRadius: 12,
    backgroundColor: '#0a0a23',
    borderWidth: 2,
    borderColor: '#00fff7',
    alignItems: 'center',
    justifyContent: 'center',
    shadowColor: '#00fff7',
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: 0.5,
    shadowRadius: 12,
  },
  startButtonText: {
    fontFamily: pixelFont,
    fontSize: scaleFont(24),
    color: '#00fff7',
    textShadowColor: 'rgba(0, 255, 247, 0.5)',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 10,
  },
});
