import React, { useEffect } from 'react';
import { View, Text, StyleSheet, Dimensions, ActivityIndicator, SafeAreaView } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { reload } from 'firebase/auth';

const { width, height } = Dimensions.get('window');
const pixelFont = 'PressStart2P_400Regular';

export default function WaitingVerificationScreen({ route, navigation }) {
  const { firebaseUser } = route.params;

  useEffect(() => {
    const interval = setInterval(async () => {
      try {
        await reload(firebaseUser);
        if (firebaseUser.emailVerified) {
          clearInterval(interval);
          navigation.replace('Games');
        }
      } catch (e) {
        // Puedes mostrar un error si quieres
      }
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <SafeAreaView style={{ flex: 1, backgroundColor: '#0a0a23' }}>
      <View style={styles.container}>
        <View style={styles.cornerDotTL} />
        <View style={styles.cornerDotTR} />
        <View style={styles.cornerDotBL} />
        <View style={styles.cornerDotBR} />
        <Ionicons name="mail-unread" size={64} color="#00fff7" style={{ marginBottom: 24 }} />
        <Text style={styles.title}>Verificando tu cuenta…</Text>
        <Text style={styles.msg}>Revisa tu correo y haz clic en el enlace de verificación. Esta ventana se cerrará automáticamente cuando completes la verificación.</Text>
        <ActivityIndicator size="large" color="#00fff7" style={{ marginTop: 32 }} />
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#0a0a23',
    alignItems: 'center',
    justifyContent: 'center',
    padding: 24,
    width: '100%',
    maxWidth: 420,
    alignSelf: 'center',
  },
  title: {
    color: '#fff',
    fontFamily: pixelFont,
    fontSize: Math.min(width * 0.07, 24),
    marginBottom: 16,
    textAlign: 'center',
    textShadowColor: '#00fff7',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 12,
  },
  msg: {
    color: '#ff2e7e',
    fontFamily: pixelFont,
    fontSize: Math.min(width * 0.032, 14),
    textAlign: 'center',
    marginBottom: Math.max(12, width * 0.03),
    paddingHorizontal: 10,
  },
  cornerDotTL: {
    position: 'absolute',
    top: 18,
    left: 18,
    width: 12,
    height: 12,
    backgroundColor: '#ff2e7e',
    borderRadius: Math.max(18, width * 0.045),
    zIndex: 2,
  },
  cornerDotTR: {
    position: 'absolute',
    top: 18,
    right: 18,
    width: 12,
    height: 12,
    backgroundColor: '#00fff7',
    borderRadius: Math.max(18, width * 0.045),
    zIndex: 2,
  },
  cornerDotBL: {
    position: 'absolute',
    bottom: 18,
    left: 18,
    width: 12,
    height: 12,
    backgroundColor: '#00fff7',
    borderRadius: Math.max(18, width * 0.045),
    zIndex: 2,
  },
  cornerDotBR: {
    position: 'absolute',
    bottom: 18,
    right: 18,
    width: 12,
    height: 12,
    backgroundColor: '#ff2e7e',
    borderRadius: Math.max(18, width * 0.045),
    zIndex: 2,
  },
});
