import React, { useRef, useEffect, useState } from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity, SafeAreaView, Animated, Modal, ActivityIndicator } from 'react-native';
import { auth, db } from '../firebaseConfig';
import { doc, getDoc } from 'firebase/firestore';
import { useFocusEffect } from '@react-navigation/native';
import {
  screenWidth as width,
  screenHeight as height,
  wp,
  hp,
  SPACING,
  FONT_SIZES,
  scaleDimension,
  scaleFont,
  getResponsiveDimension
} from '../utils/dimensions';

const pixelFont = 'PressStart2P_400Regular';

const logoSnake = require('../assets/snake.png');
const questionSymbol = require('../assets/question-symbol.png');

export default function SnakeDetailScreen({ navigation }) {
  const [stats, setStats] = useState({
    bestScore: 0,
    lastScore: 0,
    totalGames: 0,
    totalPixels: 0
  });
  const [isLoading, setIsLoading] = useState(true);
  const [showHelp, setShowHelp] = useState(false);

  useFocusEffect(
    React.useCallback(() => {
      let isActive = true;
      const loadStats = async () => {
        if (!auth.currentUser) return;
        try {
          const statsRef = doc(db, 'users', auth.currentUser.uid, 'snakeStats', 'stats');
          const statsDoc = await getDoc(statsRef);
          if (statsDoc.exists() && isActive) {
            setStats({
              bestScore: statsDoc.data().bestScore || 0,
              lastScore: statsDoc.data().lastScore || 0,
              totalGames: statsDoc.data().totalGames || 0,
              totalPixels: statsDoc.data().totalPixels || 0,
            });
          }
        } catch (error) {
          console.error('Error loading stats:', error);
        } finally {
          if (isActive) setIsLoading(false);
        }
      };
      setIsLoading(true);
      loadStats();
      return () => { isActive = false; };
    }, [])
  );

  return (
    <SafeAreaView style={styles.safeArea}>
      {/* Back button */}
      <TouchableOpacity 
        style={styles.backButton} 
        onPress={() => navigation.goBack()}
      >
        <Text style={styles.backText}>←</Text>
      </TouchableOpacity>

      <View style={styles.container}>
        <View style={styles.titleContainer}>
          <Text style={styles.title}>SNAKE</Text>
        </View>

        {/* Snake Logo */}
        <View style={styles.logoContainer}>
          <Image 
            source={logoSnake} 
            style={styles.logo} 
            resizeMode="contain"
          />
        </View>

        {/* Stats */}
        {isLoading ? (
          <ActivityIndicator size="large" color="#00fff7" />
        ) : (
          <View style={styles.statsContainer}>
            <View style={styles.statRow}>
              <Text style={styles.statText}>Mejor Puntuación: {stats.bestScore}</Text>
            </View>
            <View style={styles.statRow}>
              <Text style={styles.statText}>Última puntuación {stats.lastScore}</Text>
            </View>
            <View style={styles.statRow}>
              <Text style={styles.statText}>Partidas totales: {stats.totalGames}</Text>
            </View>
            <View style={styles.statRow}>
              <Text style={styles.statText}>Total píxeles comidos: {stats.totalPixels}</Text>
            </View>
          </View>
        )}

        {/* Help Button */}
        <TouchableOpacity 
          style={styles.helpButton}
          onPress={() => setShowHelp(true)}
        >
          <Text style={styles.helpButtonText}>?</Text>
        </TouchableOpacity>

        {/* Start Button */}
        <TouchableOpacity 
          style={styles.startButton}
          onPress={() => navigation.navigate('SnakeGameScreen')}
        >
          <Text style={styles.startButtonText}>¡START!</Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  safeArea: {
    flex: 1,
    backgroundColor: '#0a0a23',
  },
  container: {
    flex: 1,
    alignItems: 'center',
    paddingTop: height * 0.02,
  },
  backButton: {
    position: 'absolute',
    top: height * 0.02,
    left: width * 0.04,
    zIndex: 10,
  },
  backText: {
    color: '#ff2e7e',
    fontFamily: pixelFont,
    fontSize: 24,
  },
  titleContainer: {
    backgroundColor: '#6b21a8',
    paddingHorizontal: 20,
    paddingVertical: 5,
    marginBottom: height * 0.03,
  },
  title: {
    fontFamily: pixelFont,
    fontSize: scaleFont(32),
    color: '#00fff7',
    textAlign: 'center',
  },
  logoContainer: {
    width: width * 0.6,
    aspectRatio: 2,
    borderWidth: 2,
    borderColor: '#00fff7',
    backgroundColor: '#0a0a23',
    padding: 10,
    marginBottom: height * 0.03,
  },
  logo: {
    width: '100%',
    height: '100%',
  },
  statsContainer: {
    width: '90%',
    marginBottom: height * 0.03,
  },
  statRow: {
    borderWidth: 2,
    borderColor: '#00fff7',
    padding: 8,
    marginBottom: 8,
  },
  statText: {
    fontFamily: pixelFont,
    fontSize: scaleFont(12),
    color: '#00fff7',
    textAlign: 'center',
  },
  helpButton: {
    width: 30,
    height: 30,
    borderRadius: 15,
    backgroundColor: '#ff2e7e',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: height * 0.02,
  },
  helpButtonText: {
    fontFamily: pixelFont,
    fontSize: scaleFont(16),
    color: '#0a0a23',
  },
  startButton: {
    marginTop: height * 0.02,
  },
  startButtonText: {
    fontFamily: pixelFont,
    fontSize: scaleFont(24),
    color: '#00fff7',
  },
});
