import React, { useState, useEffect, useRef } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, SafeAreaView, ScrollView, Animated, Modal } from 'react-native';
import RetroButton from '../components/RetroButton';
import {
  screenWidth as width,
  screenHeight as height,
  wp,
  hp,
  SPACING,
  FONT_SIZES,
  scaleDimension,
  scaleFont,
  getResponsiveDimension
} from '../utils/dimensions';

const pixelFont = 'PressStart2P_400Regular';

const pixelStroke = [
  { textShadowOffset: { width: -2, height: 0 }, textShadowColor: '#000', textShadowRadius: 0 },
  { textShadowOffset: { width: 2, height: 0 }, textShadowColor: '#000', textShadowRadius: 0 },
  { textShadowOffset: { width: 0, height: -2 }, textShadowColor: '#000', textShadowRadius: 0 },
  { textShadowOffset: { width: 0, height: 2 }, textShadowColor: '#000', textShadowRadius: 0 },
];

export default function DuosDetailScreen({ navigation }) {
  const [showHelp, setShowHelp] = useState(false);
  const [selectedDifficulty, setSelectedDifficulty] = useState('Facil');

  // Animations
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const scaleAnim = useRef(new Animated.Value(0.9)).current;
  const titleRotateAnim = useRef(new Animated.Value(0)).current;
  const glowAnim = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    // Initial animations
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 1000,
        useNativeDriver: true,
      }),
      Animated.spring(scaleAnim, {
        toValue: 1,
        friction: 8,
        tension: 40,
        useNativeDriver: true,
      }),
    ]).start();

    // Title rotation animation
    Animated.loop(
      Animated.sequence([
        Animated.timing(titleRotateAnim, {
          toValue: 1,
          duration: 2000,
          useNativeDriver: true,
        }),
        Animated.timing(titleRotateAnim, {
          toValue: -1,
          duration: 2000,
          useNativeDriver: true,
        }),
        Animated.timing(titleRotateAnim, {
          toValue: 0,
          duration: 2000,
          useNativeDriver: true,
        }),
      ])
    ).start();

    // Glow animation
    Animated.loop(
      Animated.sequence([
        Animated.timing(glowAnim, {
          toValue: 1,
          duration: 1500,
          useNativeDriver: true,
        }),
        Animated.timing(glowAnim, {
          toValue: 0.3,
          duration: 1500,
          useNativeDriver: true,
        }),
      ])
    ).start();
  }, []);

  const handleBack = () => {
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 0,
        duration: 300,
        useNativeDriver: true,
      }),
      Animated.timing(scaleAnim, {
        toValue: 0.9,
        duration: 300,
        useNativeDriver: true,
      }),
    ]).start(() => navigation.goBack());
  };

  const titleRotation = titleRotateAnim.interpolate({
    inputRange: [-1, 0, 1],
    outputRange: ['-3deg', '0deg', '3deg'],
  });

  return (
    <SafeAreaView style={styles.safeArea}>
      <View style={styles.background}>
        <View style={styles.gridPattern} />
        <ScrollView 
          contentContainerStyle={styles.scrollContent} 
          showsVerticalScrollIndicator={false}
        >
          {/* Decorative corners */}
          <Animated.View style={[styles.cornerDotTL, { opacity: glowAnim }]} />
          <Animated.View style={[styles.cornerDotTR, { opacity: glowAnim }]} />
          <Animated.View style={[styles.cornerDotBL, { opacity: glowAnim }]} />
          <Animated.View style={[styles.cornerDotBR, { opacity: glowAnim }]} />

          {/* Back button */}
          <Animated.View style={[styles.backButton, { opacity: fadeAnim }]}>
            <TouchableOpacity 
              style={styles.backBox}
              onPress={handleBack}
            >
              <Text style={styles.backText}>←</Text>
            </TouchableOpacity>
          </Animated.View>

          <Animated.View 
            style={[
              styles.container,
              {
                opacity: fadeAnim,
                transform: [{ scale: scaleAnim }]
              }
            ]}
          >
            {/* Title */}
            <Animated.View 
              style={[
                styles.titleContainer,
                {
                  transform: [{ rotate: titleRotation }]
                }
              ]}
            >
              <Text style={[styles.title, ...pixelStroke]}>DUOS</Text>
            </Animated.View>

            {/* Stats container */}
            <View style={styles.statsContainer}>
              <View style={styles.statRow}>
                <Text style={styles.statLabel}>Victorias:</Text>
                <Text style={styles.statValue}>0</Text>
              </View>
              <View style={styles.statDivider} />
              <View style={styles.statRow}>
                <Text style={styles.statLabel}>Derrotas:</Text>
                <Text style={styles.statValue}>0</Text>
              </View>
              <View style={styles.statDivider} />
              <View style={styles.statRow}>
                <Text style={styles.statLabel}>Mejor tiempo:</Text>
                <Text style={styles.statValue}>0:00</Text>
              </View>
            </View>

            {/* Difficulty selector */}
            <View style={styles.difficultyContainer}>
              <TouchableOpacity 
                style={[
                  styles.difficultyButton,
                  selectedDifficulty === 'Facil' && styles.difficultyButtonActive
                ]}
                onPress={() => setSelectedDifficulty('Facil')}
              >
                <Text style={[
                  styles.difficultyText,
                  selectedDifficulty === 'Facil' && styles.difficultyTextActive
                ]}>Facil</Text>
              </TouchableOpacity>

              <TouchableOpacity 
                style={[
                  styles.difficultyButton,
                  selectedDifficulty === 'Dificil' && styles.difficultyButtonActive
                ]}
                onPress={() => setSelectedDifficulty('Dificil')}
              >
                <Text style={[
                  styles.difficultyText,
                  selectedDifficulty === 'Dificil' && styles.difficultyTextActive
                ]}>Dificil</Text>
              </TouchableOpacity>
            </View>

            {/* Action buttons */}
            <View style={styles.actionButtonsContainer}>
              <TouchableOpacity 
                style={styles.helpButton}
                onPress={() => setShowHelp(true)}
              >
                <Text style={styles.helpButtonText}>AYUDA</Text>
              </TouchableOpacity>

              <TouchableOpacity 
                style={styles.startButton}
                onPress={() => {
                  navigation.navigate('DuosLoading', { difficulty: selectedDifficulty });
                }}
              >
                <Text style={styles.startButtonText}>¡START!</Text>
              </TouchableOpacity>
            </View>
          </Animated.View>
        </ScrollView>

        {/* Help Modal */}
        <Modal
          visible={showHelp}
          transparent
          animationType="fade"
          onRequestClose={() => setShowHelp(false)}
        >
          <View style={styles.helpOverlay}>
            <Animated.View 
              style={[
                styles.helpBoxBorder,
                {
                  opacity: fadeAnim,
                  transform: [{ scale: scaleAnim }]
                }
              ]}
            >
              <View style={styles.helpBox}>
                <View style={styles.helpLogoBox}>
                  <Text style={[styles.helpTitle, ...pixelStroke]}>DUOS</Text>
                </View>
                <Text style={styles.helpVoice}>
                  (Voz de androide entrenado en memoria galáctica)
                </Text>
                <Text style={styles.helpIntro}>
                  <Text style={styles.helpIntroWhite}>
                    "Bienvenido al desafío de memoria más intrigante del cosmos: {"\n"}
                    <Text style={styles.helpCyan}>DUOS</Text>.{"\n"}
                    Un juego donde las parejas perdidas esperan ser encontradas...{"\n"}
                    <Text style={styles.helpPink}>¿Podrás reunirlas todas?</Text>"
                  </Text>
                </Text>
                <Text style={styles.helpSectionTitle}>¿Cómo se juega?</Text>
                <Text style={styles.helpText}>
                  • Encuentra las parejas de cartas idénticas{"\n"}
                  • Memoriza las posiciones{"\n"}
                  • Completa el tablero antes que se acabe el tiempo{"\n"}
                  • ¡Mejora tu puntuación!
                </Text>
                <TouchableOpacity
                  style={styles.helpCloseButton}
                  onPress={() => setShowHelp(false)}
                >
                  <Text style={styles.helpCloseText}>ENTENDIDO</Text>
                </TouchableOpacity>
              </View>
            </Animated.View>
          </View>
        </Modal>
      </View>
    </SafeAreaView>
  );
}

