import React, { useState, useRef, useEffect } from 'react';
import { View, Text, StyleSheet, TextInput, Dimensions, ActivityIndicator, Animated, Pressable, SafeAreaView, ScrollView } from 'react-native';
import RetroButton from '../components/RetroButton';
import { Ionicons } from '@expo/vector-icons';

const { width, height } = Dimensions.get('window');
const pixelFont = 'PressStart2P_400Regular';

const pixelStroke = [
  { textShadowOffset: { width: -4, height: 0 }, textShadowColor: '#000', textShadowRadius: 0 },
  { textShadowOffset: { width: 4, height: 0 }, textShadowColor: '#000', textShadowRadius: 0 },
  { textShadowOffset: { width: 0, height: -4 }, textShadowColor: '#000', textShadowRadius: 0 },
  { textShadowOffset: { width: 0, height: 4 }, textShadowColor: '#000', textShadowRadius: 0 },
  { textShadowOffset: { width: -4, height: -4 }, textShadowColor: '#000', textShadowRadius: 0 },
  { textShadowOffset: { width: 4, height: -4 }, textShadowColor: '#000', textShadowRadius: 0 },
  { textShadowOffset: { width: -4, height: 4 }, textShadowColor: '#000', textShadowRadius: 0 },
  { textShadowOffset: { width: 4, height: 4 }, textShadowColor: '#000', textShadowRadius: 0 },
];

export default function EmailValidatorScreen({ route, navigation }) {
  const { username } = route.params;
  const [email, setEmail] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isSent, setIsSent] = useState(false);
  const [error, setError] = useState('');

  const fadeAnim = useRef(new Animated.Value(0)).current;
  const slideAnim = useRef(new Animated.Value(40)).current;

  useEffect(() => {
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 700,
        useNativeDriver: true,
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 700,
        useNativeDriver: true,
      }),
    ]).start();
  }, []);

  const handleSend = async () => {
    if (!email.trim()) {
      setError('Por favor ingresa un email válido');
      return;
    }
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email.trim())) {
      setError('Por favor ingresa un email válido');
      return;
    }
    setIsLoading(true);
    setError('');
    setTimeout(() => {
      setIsSent(true);
      setIsLoading(false);
      setTimeout(() => navigation.navigate('Home'), 2500);
    }, 1800);
  };

  return (
    <SafeAreaView style={{ flex: 1, backgroundColor: '#0a0a23' }}>
      <ScrollView contentContainerStyle={{ flexGrow: 1, justifyContent: 'center', alignItems: 'center', minHeight: height }} showsVerticalScrollIndicator={false}>
        <View style={styles.screen}>
          <Animated.View
            style={[
              styles.animatedBlock,
              {
                opacity: fadeAnim,
                transform: [{ translateY: slideAnim }],
              },
            ]}
          >
            <Text
              style={[
                styles.title,
                styles.titleGlow,
                ...pixelStroke,
                { fontSize: Math.min(width * 0.07, 28) },
              ]}
              numberOfLines={2}
              adjustsFontSizeToFit
            >
              Verifica tu Email
            </Text>
            <View style={styles.msgBoxBorder}>
              <View style={styles.msgBox}>
                <Text style={styles.msgText}>
                  Ingresa tu correo electrónico para recibir el enlace de verificación.
                </Text>
              </View>
            </View>
            {!isSent ? (
              <>
                <View style={styles.inputBox}>
                  <TextInput
                    style={styles.input}
                    value={email}
                    onChangeText={t => { setEmail(t); setError(''); }}
                    placeholder="Email"
                    placeholderTextColor="#00fff7"
                    keyboardType="email-address"
                    autoCapitalize="none"
                    autoComplete="email"
                    editable={!isLoading}
                  />
                </View>
                {error ? <Text style={styles.errorMsg}>{error}</Text> : null}
                <RetroButton
                  title={isLoading ? 'Enviando...' : 'Enviar Verificación'}
                  onPress={handleSend}
                  disabled={isLoading}
                  style={styles.button}
                />
              </>
            ) : (
              <View style={styles.verifiedContainer}>
                <Ionicons name="checkmark-circle" size={64} color="#00fff7" />
                <Text style={styles.verifiedText}>¡Verificación enviada!</Text>
                <Text style={styles.verifiedSubText}>Revisa tu correo y sigue el enlace.</Text>
              </View>
            )}
            {isLoading && <ActivityIndicator size="large" color="#00fff7" style={styles.loadingIndicator} />}
            <Pressable
              style={({ pressed }) => [styles.backButton, pressed && styles.backButtonActive]}
              onPress={() => navigation.goBack()}
            >
              <Text style={styles.backButtonText}>Volver</Text>
            </Pressable>
          </Animated.View>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  screen: {
    flex: 1,
    backgroundColor: '#0a0a23',
    justifyContent: 'center',
    alignItems: 'center',
  },
  animatedBlock: {
    width: '100%',
    maxWidth: 400,
    alignItems: 'center',
    justifyContent: 'center',
    paddingHorizontal: 12,
    paddingVertical: 24,
    backgroundColor: 'transparent',
    borderRadius: Math.max(18, width * 0.045),
  },
  title: {
    color: '#fff',
    fontFamily: pixelFont,
    letterSpacing: 2,
    marginBottom: Math.max(12, height * 0.025),
    textAlign: 'center',
  },
  titleGlow: {
    textShadowColor: '#00fff7',
    textShadowOffset: { width: 0, height: 0 },
    textShadowRadius: 16,
  },
  msgBoxBorder: {
    borderWidth: 6,
    borderColor: '#00fff7',
    borderRadius: 16,
    padding: 6,
    backgroundColor: '#23233a',
    marginBottom: Math.max(12, height * 0.03),
    alignSelf: 'center',
    width: '100%',
    maxWidth: 340,
    shadowColor: '#00fff7',
    shadowOpacity: 0.25,
    shadowRadius: 12,
    shadowOffset: { width: 0, height: 0 },
  },
  msgBox: {
    borderWidth: 4,
    borderColor: '#ff2e7e',
    borderRadius: 12,
    backgroundColor: '#101926',
    paddingVertical: 16,
    paddingHorizontal: 12,
    alignItems: 'center',
    shadowColor: '#ff2e7e',
    shadowOpacity: 0.15,
    shadowRadius: 8,
    shadowOffset: { width: 0, height: 0 },
  },
  msgText: {
    fontFamily: pixelFont,
    fontSize: Math.min(width * 0.032, 14),
    textAlign: 'center',
    color: '#fff',
  },
  inputBox: {
    width: '100%',
    maxWidth: 340,
    alignSelf: 'center',
    marginVertical: Math.max(12, height * 0.012),
    borderWidth: 3,
    borderColor: '#00fff7',
    borderRadius: 12,
    backgroundColor: '#23233a',
    paddingHorizontal: 8,
    paddingVertical: 10,
    marginBottom: 16,
  },
  input: {
    color: '#00fff7',
    fontFamily: pixelFont,
    fontSize: Math.min(width * 0.045, 18),
    textAlign: 'center',
    paddingVertical: 8,
    paddingHorizontal: 8,
    letterSpacing: 1,
    backgroundColor: 'transparent',
    width: '100%',
    borderWidth: 0,
  },
  errorMsg: {
    color: '#ff2e7e',
    fontFamily: pixelFont,
    fontSize: Math.min(width * 0.038, 15),
    textAlign: 'center',
    marginVertical: 8,
    backgroundColor: '#23233a',
    borderRadius: 8,
    paddingHorizontal: 8,
    paddingVertical: 4,
    borderWidth: 2,
    borderColor: '#ff2e7e',
    alignSelf: 'center',
  },
  button: {
    width: '100%',
    maxWidth: 340,
    alignSelf: 'center',
    marginVertical: Math.max(12, height * 0.012),
  },
  loadingIndicator: {
    marginTop: 20,
  },
  verifiedContainer: {
    alignItems: 'center',
    marginTop: 20,
  },
  verifiedText: {
    color: '#00fff7',
    fontFamily: pixelFont,
    fontSize: Math.min(width * 0.045, 18),
    marginTop: 20,
    textAlign: 'center',
  },
  verifiedSubText: {
    color: '#ff2e7e',
    fontFamily: pixelFont,
    fontSize: Math.min(width * 0.032, 14),
    marginTop: 10,
    textAlign: 'center',
  },
  backButton: {
    width: '100%',
    maxWidth: 340,
    alignSelf: 'center',
    marginTop: Math.max(12, height * 0.018),
    borderWidth: 2,
    borderColor: '#ff2e7e',
    borderRadius: 10,
    backgroundColor: '#23233a',
    paddingVertical: 14,
    paddingHorizontal: 10,
    marginBottom: 10,
  },
  backButtonActive: {
    borderColor: '#fff',
    backgroundColor: '#ff2e7e',
    shadowColor: '#ff2e7e',
    shadowOpacity: 0.7,
    shadowRadius: 10,
    shadowOffset: { width: 0, height: 0 },
  },
  backButtonText: {
    color: '#ff2e7e',
    fontFamily: pixelFont,
    fontSize: Math.min(width * 0.045, 18),
    textAlign: 'center',
  },
});
